package JavaMultiDimensionalArrays;

public class AccessElements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[][] myNumbers = { {1, 2, 3, 4}, {5, 6, 7} };
	    System.out.println("1 => myNumbers[0][0]: "+myNumbers[0][0]); //1
	    System.out.println("2 => myNumbers[0][1]: "+myNumbers[0][1]); //2
	    System.out.println("3 => myNumbers[0][2]: "+myNumbers[0][2]); //3
	    System.out.println("4 => myNumbers[0][3]: "+myNumbers[0][3]); //4
	    System.out.println("5 => myNumbers[1][0]: "+myNumbers[1][0]); //5
	    System.out.println("6 => myNumbers[1][1]: "+myNumbers[1][1]); //6
	    System.out.println("7 => myNumbers[1][2]: "+myNumbers[1][2]); //7
	    
	}

}
