package javaArraysDefinition;

public class ChangeAnArrayElement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 String[] cars = {"Volvo", "BMW", "Ford", "Mazda"};
		 System.out.println(" Before change first element: "+cars[0]);
		   cars[0] = "Opel";
		    System.out.println(" After change first element: "+cars[0]);
	}

}
